<?php
include 'connect.php';$connect = new mysqli($hostname, $username, $password, $databaseName);	// conexion $id=$_GET["ids"];	// recuperation du form avec des GET$target=$_GET["target"];$array=explode(";",$id);	// enleve les ; en transformant le string en tableau for($i=0;$i<(count($array)-1);$i++){		if ($target == activateUser)	{		$sql="UPDATE clients SET actif=1 WHERE id='$array[$i]'";			$connect->query($sql);// met actif tout les clien avec leur id recuper 		$sql="UPDATE notifications SET comptes = comptes-1 WHERE id = 1";		$connect->query($sql);	// met a jour la basse de donner du site 	}	else if ($target == activateAnnonce)	{		$sql="UPDATE annonces SET active=1 WHERE id='$array[$i]'";			$connect->query($sql);// met actif tout les clien avec leur id recuper 		$sql="UPDATE notifications SET annonces = annonces-1 WHERE id = 1";		$connect->query($sql);	// met a jour la basse de donner du site 		$sql="SELECT email,titre,date_publication FROM annonces WHERE id = '$array[$i]'";		$result = $connect->query($sql);		while($row = $result->fetch_array())		{			$email = $row[0];			$message = "Votre annonce créée le ".$row[2].", portant le nom \'".$row[1]."\' a été validée par un administrateur.";					}		$sql="INSERT INTO notifications_utilisateur VALUES ('','$email','$message',1)";		$connect->query($sql);	// met a jour la basse de donner du site 	}	}$connect->close(); // fermeture 